import React, {useEffect, useState} from 'react'
import styled from 'styled-components'
import axiosInstance from '../../api/api.config';
import Sidebar from "./Sidebar";
import { Typography } from '@mui/material';

const Wrapper = styled.div`
    box-sizing: border-box;
`
const DashArea = styled.div`
    box-sizing: border-box;
    margin:24px; 
`
const Dashboard = async (props) => {
    const [activeNav, setActiveName] = useState('')
    const [showSales, setShowSales] = useState(false)
    const [title, setTitle] = useState('')
    useEffect(() => {
        if(activeNav === "SaleOppurtunity") {
            (async function(){
                const res = await axiosInstance.get("getSalesOpportunities")
                if(res.status === 200) {
                    setShowSales(!showSales)
                } 
                else {
                    setShowSales(!showSales)
                    setTitle(res.data.message)
                }
            })()
        }
    },[activeNav, showSales])
    return(
        <Wrapper>
            <Sidebar {...props} setNowNav={setActiveName} activeNav={activeNav}/>
            <DashArea title={title} showGrid={showSales}>
            {title && <Typography variant='h2'>{title}</Typography>}
            </DashArea>
        </Wrapper>
    )
}

export default Dashboard
